import { useState } from "react";
import MeetupList from "../components/meetups/MeetupList";

const DUMMY_DATA = [
  {
    id: 'm1',
    title: 'This is a first meetup',
    image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/f/f3/Wiki.Vojvodina_VII_Subotica_5235.jpg/1280px-Wiki.Vojvodina_VII_Subotica_5235.jpg',
    address: 'Meetupstreet 5, 12345 Meetup City',
    description: 'This is a first, amazing meetup which you definitely should not miss'
  },
  {
    id: 'm2',
    title: 'This is a second meetup',
    image: 'https://upload.wikimedia.org/wikipedia/commons/e/e9/Raichle03KJ.jpg',
    address: 'Meetupstreet 5, 12345 Meetup City',
    description: 'This is a second, amazing meetup which you definitely should not miss'
  },
]

function AllMeetupsPage() {
  const [isLoading, setIsLoading] = useState(true);

  fetch('https://react-getting-started-4c545-default-rtdb.firebaseio.com/meetups.json').then(response => {
    return response.json();
  }).then(data => {
    setIsLoading(false);
  });

  if (isLoading) {
    return <section>
      <p>Loading....</p>
    </section>
  }

  return <section>
    <h1>All Meetups</h1>
    <MeetupList meetups={DUMMY_DATA} />
  </section>
}

export default AllMeetupsPage;
