import { useState, useEffect } from "react";
import MeetupList from "../components/meetups/MeetupList";

const DUMMY_DATA = [
  {
    id: 1,
    title: 'This is a first meetup',
    image: '',
    address: 'Meetupstreet 5, 12345 Meetup City',
    description: 'This is a first, amazing meetup which you definitely should not miss.'
  },
  {
    id: 2,
    title: 'This is a second meetup',
    image: '',
    address: 'Meetupstreet 5, 12345 Meetup City',
    description: 'This is a second, amazing meetup which you definitely should not miss.'
  },
]

function AllMeetupsPage() {
  const [isLoading, setIsLoading] = useState(true);
  const [loadedMeetups, setLoadedMeetups] = useState([]);

  useEffect(() => {
    fetch(
      'https://react-getting-started-4c545-default-rtdb.firebaseio.com/meetups.json'
    )
      .then(response => {
        return response.json();
      })
      .then((data) => {
        const meetups = [];

        for (const key in data) {
          const meetup = {
            id: key,
            ...data[key]
          };

          meetups.push(meetup);
        }

        setIsLoading(false);
        setLoadedMeetups(data);
      });
  }, []);



  if (isLoading) {
    return (
      <section>
        <p>Loading....</p>
      </section>
    );
  }

  return <section>
    <h1>All Meetups</h1>
    <ul>
      {DUMMY_DATA.map((meetup) => {
        return <li key={meetup.id}>{meetup.title}</li>;
      })}
    </ul>
    <MeetupList meetups={loadedMeetups} />
  </section>
}

export default AllMeetupsPage;
